const formatText=(text,len=150)=>{let text_trim=text.trim();if(text_trim.length>len){if(text_trim.slice(len-1,len)==" "){text_trim=text_trim.slice(0,len)+"..."}else{let new_len=len;while(text_trim.slice(new_len-1,new_len)!=" "){new_len=new_len-1}text_trim=text_trim.slice(0,new_len)+"..."}}return text_trim};let body_elements=document.querySelectorAll(".blog-post__body");for(let i=0;i<body_elements.length;i++){body_elements[i].innerText=formatText(body_elements[i].innerText)}let post_status=document.createElement("div");post_status.className="blog-post__status";post_status.innerText="nyt";let first_blog_entry=document.querySelector(".blog-post");let first_blog_thumb=first_blog_entry.querySelector(".blog-post__thumb");first_blog_entry.insertBefore(post_status,first_blog_thumb);let blog_posts=document.querySelectorAll(".blog-post");for(let j=0;j<blog_posts.length;j++){blog_posts[j].addEventListener("click",(()=>{window.location="https://www.bluecity.dk/"+blog_posts[j].dataset.location}))}